local Players=game:GetService("Players")local ReplicatedStorage=game:GetService("ReplicatedStorage")local petName="Dragonfly"local petModel=ReplicatedStorage:WaitForChild("Pets"):FindFirstChild(petName)if not petModel then warn("لم يتم العثور على الحيوان: "..petName)return end local player=Players.LocalPlayer local character=player.Character or player.CharacterAdded:Wait()local root=character:WaitForChild("HumanoidRootPart")local clone=petModel:Clone()clone.Parent=workspace clone:SetPrimaryPartCFrame(root.CFrame*CFrame.new(5,0,5))
